import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const ApplicantProfilePage = () => {
  // Contoh data pelamar (gantilah dengan data aktual Anda)
  const applicantDetails = {
    name: 'Nama Pelamar',
    email: 'nama.pelamar@example.com',
    phoneNumber: '+6281234567890',
    skills: ['React', 'JavaScript', 'CSS', 'Node.js'],
    experience: 'Pengembang Frontend dengan pengalaman lebih dari 3 tahun.',
    education: 'Sarjana Teknik Informatika, Universitas Contoh',
    portfolio: 'https://www.exampleportfolio.com',
    linkedin: 'https://www.linkedin.com/in/example',
  };

  const [applicationStatus, setApplicationStatus] = useState(null);

  const handleAccept = () => {
    // Implement logic for accepting the applicant
    setApplicationStatus('Accepted');
  };

  const handleReject = () => {
    // Implement logic for rejecting the applicant
    setApplicationStatus('Rejected');
  };

  return (
    <div>
      <h1>Profil Pelamar</h1>
      <h2>{applicantDetails.name}</h2>
      <p>Email: {applicantDetails.email}</p>
      <p>Nomor Telepon: {applicantDetails.phoneNumber}</p>

      <div>
        <h3>Keahlian:</h3>
        <ul>
          {applicantDetails.skills.map((skill, index) => (
            <li key={index}>{skill}</li>
          ))}
        </ul>
      </div>

      <div>
        <h3>Pengalaman:</h3>
        <p>{applicantDetails.experience}</p>
      </div>

      <div>
        <h3>Pendidikan:</h3>
        <p>{applicantDetails.education}</p>
      </div>

      <div>
        <h3>Portofolio:</h3>
        <a href={applicantDetails.portfolio} target="_blank" rel="noopener noreferrer">
          {applicantDetails.portfolio}
        </a>
      </div>

      <div>
        <h3>LinkedIn:</h3>
        <a href={applicantDetails.linkedin} target="_blank" rel="noopener noreferrer">
          LinkedIn
        </a>
      </div>

      <div className="mt-3">
        {applicationStatus ? (
          <p>Status Aplikasi: {applicationStatus}</p>
        ) : (
          <>
            <Button variant="success" onClick={handleAccept}>
              Terima
            </Button>
            <Button variant="danger" className="ml-2" onClick={handleReject}>
              Tolak
            </Button>
          </>
        )}
      </div>
    </div>
  );
};

export default ApplicantProfilePage;
